import type { Metadata } from "next";
import "./globals.css";
import { AdminProvider } from "@/context/AdminContext";
import Navbar from "@/components/Navbar";

export const metadata: Metadata = {
  title: "SoNymous ΓÇô Anonymous Student Message Wall",
  description: "A safe, anonymous space to share your thoughts. No signup, no judgment.",
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en">
      <head>
        <link rel="preconnect" href="https://fonts.googleapis.com" />
        <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="anonymous" />
        <link
          href="https://fonts.googleapis.com/css2?family=Caveat:wght@400;500;600;700&family=Inter:wght@400;500;600;700;800&display=swap"
          rel="stylesheet"
        />
      </head>
      <body>
        <AdminProvider>
          <Navbar />
          {children}
        </AdminProvider>
      </body>
    </html>
  );
}

'use client';

import { useState, useEffect } from 'react';
import Link from 'next/link';
import { usePathname } from 'next/navigation';
import { useAdmin } from '@/context/AdminContext';

export default function Navbar() {
    const pathname = usePathname();
    const { isAuthenticated, admin, logout } = useAdmin();
    const [menuOpen, setMenuOpen] = useState(false);

    const isLanding = pathname === '/';
    const isAdmin = pathname.startsWith('/admin');

    // Close mobile menu on route change
    useEffect(() => {
        setMenuOpen(false);
    }, [pathname]);

    // Prevent body scroll when mobile menu is open
    useEffect(() => {
        if (menuOpen) {
            document.body.style.overflow = 'hidden';
        } else {
            document.body.style.overflow = '';
        }
        return () => { document.body.style.overflow = ''; };
    }, [menuOpen]);

    return (
        <nav className="navbar">
            <Link href="/" className="navbar-brand">
                ≡ƒô¥ <span>SoNymous</span>
            </Link>

            {/* Hamburger toggle ΓÇö visible only on mobile via CSS */}
            <button
                className={`hamburger ${menuOpen ? 'open' : ''}`}
                onClick={() => setMenuOpen((v) => !v)}
                aria-label="Toggle navigation menu"
                aria-expanded={menuOpen}
            >
                <span className="hamburger-bar" />
                <span className="hamburger-bar" />
                <span className="hamburger-bar" />
            </button>

            {/* Backdrop overlay for mobile */}
            {menuOpen && (
                <div
                    className="nav-backdrop"
                    onClick={() => setMenuOpen(false)}
                    aria-hidden="true"
                />
            )}

            <div className={`navbar-links ${menuOpen ? 'navbar-links--open' : ''}`}>
                {/* Landing page: minimal ΓÇö CTAs handle navigation */}
                {isLanding && (
                    <>
                        {isAuthenticated ? (
                            <Link href="/admin/dashboard" className="navbar-link">
                                Dashboard
                            </Link>
                        ) : (
                            <Link href="/admin" className="navbar-link">
                                ≡ƒöÆ Admin
                            </Link>
                        )}
                    </>
                )}

                {/* Public pages (Browse, Write): show navigation links */}
                {!isLanding && !isAdmin && (
                    <>
                        <Link href="/" className="navbar-link">Home</Link>
                        <Link href="/messages" className={`navbar-link ${pathname === '/messages' ? 'active' : ''}`}>
                            Browse
                        </Link>
                        <Link href="/create" className={`navbar-link ${pathname === '/create' ? 'active' : ''}`}>
                            Write
                        </Link>
                    </>
                )}

                {/* Admin pages: show dashboard + logout only */}
                {isAdmin && isAuthenticated && (
                    <>
                        <Link href="/" className="navbar-link">Home</Link>
                        <Link
                            href="/admin/dashboard"
                            className={`navbar-link ${pathname === '/admin/dashboard' ? 'active' : ''}`}
                        >
                            Dashboard
                        </Link>
                        <button onClick={logout} className="navbar-link" aria-label="Logout">
                            Logout ({admin?.name})
                        </button>
                    </>
                )}

                {/* Admin login page when not authenticated */}
                {isAdmin && !isAuthenticated && (
                    <Link href="/" className="navbar-link">Home</Link>
                )}
            </div>
        </nav>
    );
}

